import React from "react";
import { Account } from "./account";

export class Modify extends React.Component {
    constructor(props) {
        super(props);
    }

    state = {
        show: true,
        cookie: localStorage.getItem("cookie"),
        username: "",
        fullname: "",
        bio: "",
        tel: "",
        birthday: "",
        location: "",
        banner_img: "",
        profil_img: "",
        pwd: "",
        pwdConf: "",
        email: ""
    };

    inputChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        });
        console.table(this.state)
    };

    verifForm = (e) => {
        e.preventDefault();
        const doc = document;
        const regMail = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        const regPhone = /^[0-9]{8,}$/;
        this.state.user.length < 6 ? doc.getElementById("user").style.color = "red" : doc.getElementById("user").style.color = "green";
        this.state.pass.length < 8 ? doc.getElementById("pass").style.color = "red" : doc.getElementById("pass").style.color = "green";
        this.state.passConf.length < 8 ? doc.getElementById("passConf").style.color = "red" : doc.getElementById("passConf").style.color = "green";
        this.state.pass !== this.state.passConf ? doc.getElementById("pass").style.color = "red" : doc.getElementById("pass").style.color = "green";
        this.state.passConf !== this.state.pass ? doc.getElementById("passConf").style.color = "red" : doc.getElementById("passConf").style.color = "green";
        Date.now() - Date.parse(this.state.date) < (60 * 60 * 24 * 365.25 * 18 * 1000) ? doc.getElementById("date").style.color = "red" : doc.getElementById("date").style.color = "green";
        !this.state.mail.match(regMail) ? doc.getElementById("mail").style.color = "red" : doc.getElementById("mail").style.color = "green";
        !this.state.phone.match(regPhone) ? doc.getElementById("phone").style.color = "red" : doc.getElementById("phone").style.color = "green";
        if (this.state.user.length < 6 || this.state.pass !== this.state.passConf || Date.now() - Date.parse(this.state.date) < (60 * 60 * 24 * 365.25 * 18 * 1000) ||  !this.state.mail.match(regMail) || this.state.pass.length < 8 || !this.state.phone.match(regPhone)){
            doc.getElementById("error").innerHTML = "Verify your information";
            return false;
        }
        else {
            doc.getElementById("error").innerHTML = "";
            this.sendForm();
            return true;
        }
    };

    render() {
        if(this.state.show === true) {
            return (
                <div className="card text-center" id="modif">
                    <div className="card-header text-twitter-4">
                        <h2>Modify</h2>
                    </div>
                    <div className="card-body text-twitter-5">
                        <div id="error" className="text-center text-danger"/>
                        <div className="row">
                            <div className="col-md-3">
                                <label htmlFor="username">Username:</label>
                                <input className="u-full-width" type="text" placeholder="New username"
                                       id="username" onChange={this.inputChange}/>
                            </div>
                            <div className="col-md-3">
                                <label htmlFor="fullname">Pseudo:</label>
                                <input className="u-full-width" type="text" placeholder="New pseudo" id="fullname" onChange={this.inputChange}/>
                            </div>
                            <div className="col-md-3">
                                <label htmlFor="bio">Bio:</label>
                                <input className="u-full-width" type="text" placeholder="New Bio" id="bio" onChange={this.inputChange}/>
                            </div>
                            <div className="col-md-3">
                                <label htmlFor="location">Locations:</label>
                                <input className="u-full-width" type="text" placeholder="New locations"
                                       id="location" onChange={this.inputChange}/>
                            </div>
                            <div className="col-md-3">
                                <label htmlFor="email">Mail:</label>
                                <input className="u-full-width" type="text" placeholder="New mail" id="email" onChange={this.inputChange}/>
                            </div>
                            <div className="col-md-3">
                                <label htmlFor="tel">Phone:</label>
                                <input className="u-full-width" type="text" placeholder="New phone" id="tel" onChange={this.inputChange}/>
                            </div>
                            <div className="col-md-3">
                                <label htmlFor="birthday">Birthday:</label>
                                <input className="u-full-width" type="date" id="birthday" onChange={this.inputChange}/>
                            </div>
                            <div className="col-md-3">
                                <form method="post" action="" encType="multipart/form-data">
                                    <label className="custom-file">
                                        <input type="file" id="modifProfilImg" className="custom-file-input"/>
                                            <span className="btn btn-twitter-5 custom-file-control">Profile image</span>
                                    </label>
                                </form>
                            </div>
                            <div className="col-md-3">
                                <form method="post" action="" encType="multipart/form-data">
                                    <label className="custom-file" htmlFor="modifBannerImg">
                                        <input type="file" id="modifBannerImg" className="custom-file-input"/>
                                            <span className="btn btn-twitter-5 custom-file-control">Banner image</span>
                                    </label>
                                </form>
                            </div>
                            <div className="col-md-3">
                                <label htmlFor="pwd">Password:</label>
                                <input className="u-full-width" type="password" placeholder="New password"
                                       id="pwd" onChange={this.inputChange}/>
                            </div>
                            <div className="col-md-3">
                                <label htmlFor="pwdConf">New Password:</label>
                                <input className="u-full-width" type="password" placeholder="New password"
                                       id="pwdConf" onChange={this.inputChange}/>
                            </div>
                            <div className="col-md-3">
                                <article className="btn btn-danger" id="delete">Delete</article>
                            </div>
                        </div>
                    </div>
                    <div className="card-footer">
                        <div className="row">
                            <article className="col-lg-5"/>
                            <a id="modifValid" href="#" className="col-lg-2 btn btn-twitter-5" onClick={this.verifForm}>
                                <h2>Valid</h2>
                            </a>
                            <article className="col-lg-5"/>
                        </div>
                    </div>
                </div>
            );
        }
        else if(this.state.show === "account")
            return (
                <Account />
            );
    }
}
