import React from "react";

export class Wall extends React.Component {
    constructor(props) {
        super(props);
    }

    state = {
        cookie: localStorage.getItem("cookie"),
        isLoaded: false

    };

    inputChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        });
        console.log(this.state)
    };

    componentDidMount() {
        fetch("http://localhost:8080/getWallTweet", {
            method: "POST",
            body: JSON.stringify({ cookie: this.state.cookie}),
            headers: {
                "content-type": "application/json"
            }
        }).then((resp) => resp.json())
            .then(val => {
                this.setState({tweet: val});
                this.setState({ isLoaded: true });
                console.log(this.state)
            });
    }

    render() {
        if (!this.state.isLoaded) {
            return <div>Loadingâ€¦</div>;
        } else {
            return (
                <div className="container" id="showTweet">
                    {this.state.tweet.map(x => (
                            <div className="card">
                                <div className="card-header bg-twitter-6 text-white d-flex justify-content-between">Tweet</div>
                                {x.content}
                            </div>
                        )
                    )
                    })}
                </div>
            );
        }
    }
}
