import "./bootdouille.css";
import "./skeleton.css";
import React from "react";
import { Login,  Account } from "./index.jsx";

class App extends React.Component {
  constructor(props) {
    super(props);
    if(localStorage.getItem("cookie") !== null) {
      this.state = {
        isLogginActive: true
      };
    }
    else this.state = {
      isLogginActive: false
    };
  }

  render() {
    const { isLogginActive } = this.state;
    return (
        <div className="App">
          <div className="login">
            <div className="container-fluid" ref={ref => (this.container = ref)}>
              {isLogginActive && (
                  <Account containerRef={ref => (this.current = ref)} />
              )}
              {!isLogginActive && (
                  <Login containerRef={ref => (this.current = ref)} />
              )}
            </div>
          </div>
        </div>
    );
  }
}

export default App;
