import React from "react";

export class Account extends React.Component {
    constructor(props) {
        super(props);
    }

    state = {
        cookie: localStorage.getItem("cookie"),
        show: true,
        id_u: "",
        username: "",
        fullname: "",
        bio: "",
        tel: "",
        birthday: "",
        location: "",
        verified: "",
        banner_img: "",
        profil_img: "",
        nbr_follow: "",
        nbr_abo: "",
    };

    inputChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        });
        console.log(this.state)
    };

    componentDidMount() {
        fetch("http://localhost:8080/getInfo", {
            method: "POST",
            body: JSON.stringify(this.state),
            headers:{
                "content-type": "application/json",
            }
        }).then((res) => res.json())
            .then(data => {
                console.table(data);
                for(let i in data.info)
                    console.log(data.info[i].name)
                    //this.setState(keys[i], data.info[i]);
            });
    }

    render() {
            return (
                <div className="container">
                    <div className="card text-center" id="account">
                        <div className="card-header text-twitter-4">
                            <h2>Your Account</h2>
                        </div>
                        <img src="" className="card-img-top" id="bannerImage"/>
                            <div className="card-body">
                                <h5 className="card-title">
                                    <p id="username" className="text-muted">Nom Prenom</p>
                                    <p id="pseudo"/>
                                </h5>
                                <div className="row">
                                    <div className="col-md-4">
                                        <img id="profilImage" className="mx-auto img-fluid d-block" alt="avatar"/>
                                    </div>
                                    <div className="col-md-4">
                                        <p className="text-muted" id="subscribeDate"/>
                                        <hr/>
                                            <p>Bio:</p>
                                            <p id="bio"/>
                                    </div>
                                    <div className="col-md-4">
                                        <h6 className="text-twitter-5">Followers:</h6>
                                        <article id="follow"/>
                                        <p/>
                                        <h6 className="text-twitter-5">Following:</h6>
                                        <article id="abo"/>
                                        <hr/>
                                            <h6>Age</h6>
                                            <article id="ageProfil"/>
                                    </div>
                                </div>
                            </div>
                            <div className="card-footer" id="tweetTweet">
                                <div className="text-twitter-2" id="sizeTweet"/>
                                <div>
                                    <form className="row" action="" encType="multipart/form-data">
                                        <article className="col-md-3"/>
                                        <textarea id="tweet" placeholder="Max length: 140" className="col-lg-5"/>
                                        <label className="custom-file col-md-1" htmlFor="tweetImg">
                                            <input type="file" id="tweetImg" className="custom-file-input"/>
                                                <span className="btn btn-twitter-5 custom-file-control">Img</span>
                                        </label>
                                        <a id="tweeter" href="#" className="col-md-3 btn btn-twitter-5">
                                            <h2>Tweet</h2>
                                        </a>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                );
        }
}
