import React from "react";

export class Wall extends React.Component {
    constructor(props) {
        super(props);
    }

    state = {
        cookie: localStorage.getItem("cookie"),
        isLoaded: false

    };

    like = (e) => {
        fetch("http://localhost:8080/like", {
            method: "POST",
            body: JSON.stringify({
                cookie: this.state.cookie,
                id: e.target.getAttribute("data")
            }),
            headers: {
                "content-type": "application/json"
            }
        }).then((resp) => resp.json())
            .then(val => {
                console.table(val);
            });
    };

    inputChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        });
        console.log(this.state)
    };

    componentDidMount() {
        fetch("http://localhost:8080/getWallTweet", {
            method: "POST",
            body: JSON.stringify({ cookie: this.state.cookie}),
            headers: {
                "content-type": "application/json"
            }
        }).then((resp) => resp.json())
            .then(val => {
                this.setState({tweet: val});
                this.setState({ isLoaded: true });
                console.log(this.state)
            });
    }

    render() {
        if (!this.state.isLoaded) {
            return <div>Loadingâ€¦</div>;
        } else {
            return (
                <div className="container" id="showTweet">
                    {this.state.tweet.map(x => (
                            <div className="card">
                                <div className="card-header bg-twitter-6 text-white d-flex justify-content-between">
                                    Tweet
                                    <a data={x.id} className="deleteTweet btn btn-sm btn-danger">X</a>
                                </div>
                                <div className="card-body">
                                    <div className="row">
                                        <img src={x.profil_img} alt="Profile image" className="card-img-top col-md-3" style={{borderRadius: "999999px"}}/>
                                        <div className="col-md-3">
                                            <p className="text-twitter-5">{"@"+x.username}</p>
                                        </div>
                                        <div className="col-md-3">
                                            <p className="text-muted">{Date(Date.parse(x.date)).substr(0,15)}</p>
                                        </div>
                                        <div className="col-md-12 text-center" dangerouslySetInnerHTML={{__html: x.content}}/>
                                        <img className="col-md-12" src={x.paths}/>
                                    </div>
                                </div>
                                <div className="card-footer d-flex justify-content-between">
                                    <button data={x.user_id} className="like btn btn-sm btn-twitter-2" onClick={this.like}>Like</button>
                                    <article className="text-twitter-5" dangerouslySetInnerHTML={{__html: x.likes+"&hearts;"}}/>
                                    <article className="text-twitter-5" dangerouslySetInnerHTML={{__html: x.likes+"&#8362;"}}/>
                                    <button data={x.user_id} className="like btn btn-sm btn-twitter-2">Retweet</button>
                                </div>
                            </div>
                        )
                    )
                    }
                </div>
            );
        }
    }
}
