import React from "react";
import {Account} from "./account";
import {Modify} from "./modify";
import {Wall} from "./wall";

export class NavbarTweet extends React.Component {
    constructor(props) {
        super(props);
    }

    state = {
      showAccount: true,
      showModify: false,
       showResults: false,
      showMessage: false
    };

    showModif = (e) => {
        this.setState({ showResults: false });
        this.setState({ showModify: true });
        this.render();
    };

    showResults = (e) => {
        this.setState({ showResults: true });
        this.setState({ showModify: false });
        this.render();
    };

    disconnect = (e) => {
      localStorage.setItem("cookie", "");
      window.location.reload();
    };

    render() {
        return (
            <>
                <nav className="navbar bg-twitter-5 text-white">
                    <a className="navbar-brand" onClick={this.showAccount}>Tweet</a>
                    <a className="nav-item nav-link">Messages</a>
                    <a className="nav-link" onClick={this.showModif}>Account</a>
                    <a className="nav-link" onClick={this.disconnect}>Disconnect</a>
                    <input className="" type="search" placeholder="Search" aria-label="Search" onClick={this.showResults}/>
                    <button className="btn btn-twitter-4 " type="submit">Search</button>
                </nav>
                <Wall data={this.state.showResults}/>
                <Modify data={this.state.showModify}/>
            </>
        );
    }
}
