import React from "react";

export class Register extends React.Component {
    constructor(props) {
        super(props);
    }

    state = {
        user: null,
        pass: null,
        passConf: null,
        mail: null,
        phone: null,
        date: null
    };

    inputChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        });
        console.log(this.state)
    };

    verifForm = (e) => {
      e.preventDefault();
        const regMail = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        const regPhone = /^[0-9]{8,}$/;
        this.state.user.length < 6 ? $("#username").css("color", "red") : $("#username").css("color", "green");
        this.state.pass.length < 8 ? $("#password").css("color", "red") : $("#password").css("color", "green");
        this.state.passConf.length < 8 ? $("#passwordConf").css("color", "red") : $("#passwordConf").css("color", "green");
        this.state.pass !== this.state.passConf ? $("#password").css("color", "red") : $("#password").css("color", "green");
        this.state.passConf !== this.state.pass ? $("#passwordConf").css("color", "red") : $("#passwordConf").css("color", "green");
        Date.now() - Date.parse(this.state.date) < (60 * 60 * 24 * 365.25 * 18 * 1000) ? $("#date").css("color", "red") : $("#date").css("color", "green");
        !this.state.mail.match(regMail) ? $("#mail").css("color", "red") : $("#mail").css("color", "green");
        !this.state.phone.match(regPhone) ? $("#phone").css("color", "red") : $("#phone").css("color", "green");
        if (this.state.user.length < 6 || this.state.pass !== this.state.passConf || Date.now() - Date.parse(this.state.date) < (60 * 60 * 24 * 365.25 * 18 * 1000) ||  !this.state.mail.match(regMail) || this.state.pass.length < 8 || !this.state.phone.match(regPhone)){
            $("#error").text("Relisez vous.");
            return false;
        }
        else {
            $("#error").text("");
            return true;
        }
    };

    render() {
        return (
            <form>
                <div className="container">
                    <div className="row">
                        <div className="four columns">
                            <label htmlFor="username">Username</label>
                            <input className="u-full-width" type="text" placeholder="Username" id="user" onChange={this.inputChange}/>
                        </div>
                        <div className="four columns">
                            <label htmlFor="password">Password</label>
                            <input className="u-full-width" type="password" placeholder="Password" id="pass" onChange={this.inputChange}/>
                        </div>
                        <div className="four columns">
                            <label htmlFor="passwordConf">Password Confirm</label>
                            <input className="u-full-width" type="password" placeholder="Password" id="passConf" onChange={this.inputChange}/>
                        </div>
                    </div>
                    <div className="row">
                        <div className="four columns">
                            <label htmlFor="date">Birthday</label>
                            <input className="u-full-width" type="date" id="date" onChange={this.inputChange}/>
                        </div>
                        <div className="four columns">
                            <label htmlFor="mail">Mail</label>
                            <input className="u-full-width" type="text" placeholder="Mail" id="mail" onChange={this.inputChange}/>
                        </div>
                        <div className="four columns">
                            <label htmlFor="phone">Phone</label>
                            <input className="u-full-width" type="tel" placeholder="Phone" id="phone" onChange={this.inputChange}/>
                        </div>
                    </div>
                    <div className="row">
                        <div className="two columns">
                            <label htmlFor="submit"/>
                            <button className="button-primary" id="submit" onClick={this.verifForm}>Register</button>
                        </div>
                        <div className="two columns">
                            <label htmlFor="submit"/>
                            <button className="button" id="login">Login</button>
                        </div>
                    </div>
                </div>
            </form>
        );
    }
}
