import "./bootdouille.css";
import "./skeleton.css";
import React from "react";
import { Login, Register, Wall } from "./index.jsx";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLogginActive: true
    };
  }

  render() {
    const { isLogginActive } = this.state;
    return (
        <div className="App">
          <div className="login">
            <div className="container-fluid" ref={ref => (this.container = ref)}>
              {isLogginActive && (
                  <Wall containerRef={ref => (this.current = ref)} />
              )}
              {!isLogginActive && (
                  <Login containerRef={ref => (this.current = ref)} />
              )}
            </div>
          </div>
        </div>
    );
  }
}

export default App;
